CREATE TABLE Building(
BuildingID VARCHAR2(40) PRIMARY KEY,
BuildingName VARCHAR2(40),
BuildingShape SDO_GEOMETRY
);

CREATE TABLE Student(StudentID VARCHAR2(40),StudentShape SDO_GEOMETRY);

CREATE TABLE TramStop(TramStopID VARCHAR2(10)PRIMARY KEY,Radius INT, TramStopShape SDO_GEOMETRY);

SET ESCAPE '\';





VALUES ('Building','BuildingShape',
        SDO_DIM_ARRAY(
             SDO_DIM_ELEMENT('x', 0, 1000, 0.005),
             SDO_DIM_ELEMENT('Y', 0, 1000, 0.005)
        ), NULL);
	
VALUES ('Student','StudentShape',
        SDO_DIM_ARRAY(
             SDO_DIM_ELEMENT('x', 0, 1000, 0.005),
             SDO_DIM_ELEMENT('Y', 0, 1000, 0.005)
